 <script lang="ts">
 import { Vue, Component, Prop, Watch } from 'vue-property-decorator'

 interface User {
   familyName: string
   givenName: number
 }

 @Component
 export default class Title extends Vue {
    flg: boolean = false;
    titlePosition: number = 30;
    lastY:number = 0;
    timerObj:number = null;
    titleTopPosition:number = 5;


    get titleHeight(){
        return this.titlePosition + '%'
    }

    mounted() {
        this.flg = true
        window.addEventListener('scroll', this.onScroll);
        setTimeout(function(){
            this.timerObj = setInterval(function() {this.count()}.bind(this), 10)
        }.bind(this), 2000);

    }
    count(){
        if(this.titlePosition > this.titleTopPosition){
            this.titlePosition = this.titlePosition - 0.1;
        }else{
          this.titlePosition = this.titleTopPosition;
          clearInterval(this.timerObj);
        }

    }
    //methods
    mainTitleEnter(el) {
        el.style.transitionDelay = 3000 + 'ms'
    }
    subTitleEnter(el) {
        el.style.transitionDelay = 5000 + 'ms'
    }
    officialTitleEnter(el) {
        el.style.transitionDelay = 6000 + 'ms'
    }
    carTitleEnter(el) {
        el.style.transitionDelay = 6500 + 'ms'
    }
    scrollTitleEnter(el) {
        el.style.transitionDelay = 7000 + 'ms'
    }
    onScroll () {
      this.titlePosition += (window.scrollY - this.lastY) / 10;
      this.lastY = window.scrollY;
    }

    @Watch('titlePosition')
    countByWatched(newVal, oldVal) {
        if(newVal < this.titleTopPosition){
            this.titlePosition = this.titleTopPosition
        }
    }    

 }
 </script>

